import { Component } from "react";
import { MediaCard } from "@shopify/polaris";
import { getMarsPhotos } from "../../utils/dataUtils";
export default class MarsPhotos extends Component {
  componentDidMount() {
    state = {
      marsPhotos: null,
    };

    getMarsPhotos().then((res) => {
      console.log(res.data);
    });
  }

  render() {
    // const text = this.state.liked ? "Like" : "Liked";
    console.log("MARS PHTOS    :", this.state.marsPhotos);
    const APOD = this.state.APOD;
    if (this.state.marsPhotos === null) {
      return (
        <>
          <img
            className="marsphotos__loading"
            src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif"
            alt="loading"
          />
        </>
      );
    }
    return (
      <MediaCard
        title="REACH FOR THE STARS!"
        primaryAction={{
          content: "Astronomy Picture of the Day",
          onClick: this.handleClick,
        }}
        description="Discover a universe where Shopify can power up your entrepreneurial journey."
        popoverActions={[{ content: "Dismiss", onAction: () => {} }]}
        size="small"
      >
        <img
          className="Polaris-MediaCard__hero-image"
          alt="Stars"
          src={"some Image"}
        />
        <button></button>
      </MediaCard>
    );
  }
}
